import React, { useState } from "react";
import "../styles/Login.css";
import { FaEnvelope, FaLock, FaEye, FaEyeSlash } from "react-icons/fa";
import logo from "../assets/eFormX.png";
import axios from "axios";

function Login({ goRegister, goForgot, setUser }) {
  const [email, setEmail] = useState(""); // store email
  const [password, setPassword] = useState(""); // store password
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(""); // error message

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://localhost:8000/api/login", {
        email,
        password,
      });
      setUser(res.data); // set superadmin info in App.js
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="login-container">
      <div className="logo-container">
        <img src={logo} alt="eFormX Logo" className="logo" />
      </div>

      <div className="login-card">
        <h2>Welcome</h2>

        <div className="input-group">
          <FaEnvelope className="icon" />
          <input
            type="email"
            placeholder="name@gmail.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="input-group password-group">
          <FaLock className="icon" />
          <input
            type={showPassword ? "text" : "password"}
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <span
            className="password-toggle"
            onClick={() => setShowPassword((prev) => !prev)}
          >
            {showPassword ? <FaEyeSlash /> : <FaEye />}
          </span>
        </div>

        {error && <p className="error-message">{error}</p>}

        <button className="login-btn" onClick={handleLogin}>
          Sign In
        </button>

        <div className="links">
          <span onClick={goForgot}>Forgot Password</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
