import { useState } from "react";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import SuperAdminDashboard from "./components/SuperAdminDashboard";

function App() {
  const [page, setPage] = useState("login"); // start on login
  const [userEmail, setUserEmail] = useState(""); // store the email

  // Called from Login when user submits email/password
  const handleLogin = (email) => {
    setUserEmail(email);
    if (email === "superadmin@example.com") {
      setPage("superAdmin");
    } else {
      setPage("dashboard");
    }
  };

  return (
    <>
      {page === "login" && <Login onLogin={handleLogin} />}
      {page === "dashboard" && <Dashboard userEmail={userEmail} />}
      {page === "superAdmin" && <SuperAdminDashboard />}
    </>
  );
}

export default App;
